    const[deezer,setDeezer]=useState()
const deezerCharts_1=async()=>{ 
    const results =await fetch("https://deezerdevs-deezer.p.rapidapi.com/playlist/1479458365", {
        "method": "GET",
        "headers": {
            "x-rapidapi-key": "e29141c4e6msh102d2b1926a7361p1b674ejsnca4accbfc562",
            "x-rapidapi-host": "deezerdevs-deezer.p.rapidapi.com"
        }
    }) 
     const data= await results.json()
     console.log(data);
     setDeezer(data);

 }

const fetchCharts=async()=>{ 
   const results =await fetch(`${URL}&method=chart.gettoptracks`) 
    const data= await results.json()
    console.log(data.tracks.track);
    setCharts(data.tracks.track);

}
if (charts!==undefined){console.log(charts);}
const fetchArtists=async()=>{ 
    const results =await fetch(`${URL}&method=chart.gettopartists`) 
    const data= await results.json()
    console.log(data.artists.artist);
    setArtists(data.artists.artist);

    }
if (artists!==undefined){console.log(artists);}

const fetchTags=async()=>{
    const results =await fetch(`${URL}&method=chart.gettoptags`) 
    const data= await results.json()
    console.log(data.tags.tag);
    setTags(data.tags.tag);

    }
    if (tags!==undefined){console.log(tags);}

    const fetchLocation=async()=>{ 
        const results =await fetch(`${URL}&method=geo.gettoptracks&country=india`) 
         const data= await results.json()
         console.log(data.tracks.track);
         setLocation(data.tracks.track);

     }
     if (location!==undefined){console.log(location);}

     const fetchRecent=async()=>{ 
        const results =await fetch(`${URL}&method=user.getrecenttracks&user=deucaleon18&page=1`) 
         const data= await results.json()
         console.log(data.recenttracks.track);
         setRecents(data.recenttracks.track);
    }
     if (recents!==undefined){console.log(recents);}
useEffect(()=>{
fetchCharts();
fetchArtists();
fetchTags();
fetchLocation();
fetchRecent();
deezerCharts_1();
},[])

