    {recents.map((recent)=>{
        const{name,mbid,url}=recent
        return <div  className="box">
        <img src={img} alt="" />
        <h4>{name}</h4>
        <button><a href={url}>PLAY</a></button>
    </div>
    })
    }) 
</div> 
<div className="title">TOP TRACKS</div>
(<div className="arrange">
    {charts.map((chart)=>{
        const{name,duration,mbid,image}=chart
        return <div  className="box">
        <img src={img}alt="" />
        <h4>{name}</h4>
        <button>PLAY</button>
    </div>
    })})
</div>
   <div className="title">TOP ARTISTS</div>
   <div className="arrange1">
   {artists.map((artist)=>{
        const{name,mbid}=artist
        return <div  className="box">
        <img src={img} alt="" />
        <h4>{name}</h4>
        <button>PLAY</button>
    </div>
    })})
   </div>
   <div className="title">TOP TAGS</div>
   <div className="arrange2">
   {tags.map((tag)=>{
        const{name,mbid}=tag
        return <div  className="box">
        <img src={img} alt="" />
        <h4>{name}</h4>
        <button>PLAY</button>
    </div>
    })})
   </div>
   <div className="title">TOP SONGS IN YOUR REGION</div>
   <div className="arrange2">
    {location.map((locate)=>{
        const{name,mbid}=locate
        return <div  className="box">
        <img src={img} alt="" />
        <h4>{name}</h4>
        <button>PLAY</button>
    </div>
    })}) 
</div> 
</div>     
